mapper.update:
UPDATE PS_Head_Teachers SET

    NAME = #{name},


    grade = #{grade},


    class = #{sClass},


    del_yn = #{delYn},

update_id = #{updateId}, update_date = #{updateDate} WHERE ID = #{id};

mapper.select:
SELECT HT.*
FROM PS_Head_Teachers HT
where  HT.del_yn='N'

    AND  HT.school_id = #{schoolId}


    AND   HT.name = #{name}


    AND   HT.grade = #{grade}


    AND   HT.class = #{sClass}
