mapper.update:
UPDATE PS_Head_Teachers SET

    NAME = #{name},


    grade = #{grade},


    class = #{sClass},

update_id = #{updateId}, update_date = SYSDATE()
WHERE ID = #{id}

AND school_id=#{schoolId}

mapper.delete:
UPDATE PS_Head_Teachers SET  del_yn = 'Y',update_id = #{updateId}, update_date = SYSDATE()
WHERE ID = #{id}

AND  school_id = #{schoolId}

AND del_yn = 'N'

mapper.select:
SELECT HT.*,(Select name from PS_School SL where HT.school_id=SL.id AND del_yn='N') AS schoolName
FROM PS_Head_Teachers HT
where  HT.del_yn='N'

    AND  HT.school_id = #{schoolId}



    AND   HT.name like #{nameLike}


    AND   HT.grade = #{grade}


    AND   HT.class = #{sClass}
