mapper.select:
select  concat(XY.grade , '/' , XY.class) as grade_class,
       XY.head_teacher_id,
       XY.name,sum(XX.amount_debt) as debit,
       sum(XX.claims) as claims,sum(XX.balance) as balance,
       case when sum(XX.balance) > 0 then 0 else 1 end isHightColor,
       XX.service_id,
       (select name from ps_service svc where svc.id = XX.service_id) as name_service
from (
     select X1.student_id,X1.service_id,X1.amount_debt,X1.claims,(X1.amount_debt - X1.claims) as balance
     from (
            select sd.student_id,sd.service_id,sd.amount_debt,sum(bs.claims) as claims
            from ps_bank_statement bs , ps_students_debts sd
            where bs.reference_no = sd.reference_no


                    #{item}


            and sd.debit_date between #{fromDate} and  #{toDate}
            and bs.bank_statement_date between #{fromDate} and  #{toDate}
      group by sd.student_id,sd.service_id,sd.amount_debt
  ) X1
) XX,
(select s.id as student_id,ht.id as head_teacher_id,ht.name,ht.class,ht.grade,ht.school_id from ps_students s, ps_head_teachers ht
where s.class=ht.class
and s.grade =ht.grade
and s.school_id= ht.school_id) XY
where
    XX.student_id= XY.student_id

    and XY.school_id = #{schoolId}


    and XY.grade = #{grade}

group by concat(XY.grade , '/' , XY.class),XY.head_teacher_id,XY.name

select  concat(XY.grade , '/' , XY.class) as grade_class,XY.head_teacher_id,XY.name,XX.service_id,(select name from ps_service where XX.service_id = id) as name_service,sum(XX.amount_debt) as debit,sum(XX.claims) as claims,SUM(XX.balance) as balance,case when SUM(XX.balance) > 0 then 0 else 1 end isHightColor
from (
        select X1.student_id,X1.service_id,X1.amount_debt,X1.claims,(X1.amount_debt - X1.claims) as balance
        from (
              select sd.student_id,sd.service_id,sd.amount_debt,sum(bs.claims) as claims
              from ps_bank_statement bs , ps_students_debts sd
              where bs.reference_no = sd.reference_no


                        #{item}


              and sd.debit_date between #{fromDate} and  #{toDate}
              and bs.bank_statement_date between #{fromDate} and  #{toDate}
              group by sd.student_id,sd.service_id,sd.amount_debt
          ) X1
) XX,
(select s.id as student_id,ht.id as head_teacher_id,ht.name,ht.class,ht.grade,ht.school_id from ps_students s, ps_head_teachers ht
 where s.class=ht.class
   and s.grade =ht.grade
   and s.school_id= ht.school_id) XY
where
    XX.student_id= XY.student_id

    and XY.school_id = #{schoolId}


    and XY.grade = #{grade}


    AND XY.head_teacher_id = #{headTeacherId}

group by concat(XY.grade , '/' , XY.class),XY.head_teacher_id,XY.name,XX.service_id
