mapper.update:
UPDATE PS_Service SET

    NAME = #{name},

update_id = #{updateId}, update_date = SYSDATE() WHERE ID = #{id}

    AND  school_id = #{schoolId}

mapper.select:
SELECT S.*,(Select name from PS_School SL where S.school_id=SL.id AND del_yn='N') AS schoolName
FROM PS_Service S
where  S.del_yn='N'


    AND   S.name like #{nameLike}


    AND  S.school_id = #{schoolId}
