mapper.update:
UPDATE PS_Students_Debts SET
quantity = #{quantity}
,amount_debt = #{amountDebt}
, update_id = #{updateId}
, update_date = SYSDATE()
WHERE del_yn = 'N'

    AND  id = #{id}


    AND  service_id = #{serviceId}


    AND  school_id = #{schoolId}


    AND   student_id = #{studentId}


    AND  debit_date = #{debitDate}


    AND   grade = #{grade}


    AND   class = #{sClass}

UPDATE PS_Students_Debts SET
purpose = #{purpose}
, update_id = #{updateId}
, update_date = SYSDATE()
WHERE del_yn = 'N'

    AND id = #{id}


    AND task_id = #{taskId}

mapper.delete:
UPDATE PS_Students_Debts  SET
    del_yn = 'Y'
                     , update_id = #{updateId}
                     , update_date = SYSDATE()
WHERE del_yn = 'N'

    AND  id = #{id}


    AND  service_id = #{serviceId}


    AND  school_id = #{schoolId}


    AND   suppliers_id = #{suppliersId}


    AND   debit_date = #{debitDate}


    AND   grade = #{grade}


    AND   class = #{sClass}


    AND   task_id = #{taskId}

mapper.select:
select sd.*,s.name,(select name from PS_Service where id= sd.service_id) as service_name , s.grade, s.class
from PS_Students_Debts sd, PS_Students s
where sd.student_id=s.id and sd.del_yn='N'

    AND  sd.service_id = #{serviceId}


    AND  sd.school_id = #{schoolId}


    AND   sd.grade = #{grade}


    AND   sd.class = #{sClass}


    AND   sd.suppliers_id = #{suppliersId}


    AND   sd.student_id = #{studentId}


    AND  sd.debit_date = #{debitDate}


    AND   sd.task_id = #{taskId}

select s.id as student_id,s.name,concat(concat(LPAD(S.id, 4, '0') , '-' , S01.supplier_id,'-',#{serviceId}))  as reference_no, S01.name as service_name, #{price} as price
from PS_Students s
left join PS_Service S01 on S01.id = #{serviceId}
where s.del_yn='N'

    AND  s.school_id = #{schoolId}


    AND   s.grade = #{grade}


    AND   s.class = #{sClass}

select sd.id,sd.debit_date,sd.grade,sd.class,(select name from onetouch.PS_Service where sd.service_id=id) as service_name,sd.purpose,sd.price ,sd.suppliers_id,sd.service_id,sd.school_id from onetouch.PS_Students_Debts sd where sd.debit_date between #{fromDate} and #{toDate} and sd.del_yn='N'
AND sd.school_id = #{schoolId}
group by sd.debit_date,sd.grade,sd.class,sd.service_id,sd.school_id, sd.id

select count(1)
from onetouch.PS_Students_Debts sd
where sd.reference_no = #{referenceNo}

    AND  sd.school_id = #{schoolId}

	select T01.id, T01.school_id, T01.grade, T01.class, T01.service_id, S01.name as service_name, T01.note, T01.debit_date, T01.del_yn, T01.insert_id, T01.insert_date, T01.update_id, T01.update_date
	from onetouch.PS_Students_Debts_Task T01
	left join onetouch.PS_Service S01 on T01.service_id = S01.id and S01.del_yn = 'N'
	where 1=1
		AND T01.del_yn = 'N'

        AND T01.id = #{taskId}


        AND T01.debit_date between #{fromDate} and #{toDate}


        AND T01.school_id = #{schoolId}

	select count(T01.id)
	from onetouch.PS_Students_Debts_Task T01
	where 1=1
		AND T01.del_yn = 'N'

        AND T01.id = #{taskId}


        AND T01.school_id = #{schoolId}

mapper.delete:
UPDATE onetouch.PS_Students_Debts_Task SET del_yn = 'Y', update_id = #{updateId}, update_date = SYSDATE()
WHERE del_yn = 'N'

    AND id = #{id}

mapper.update:
        UPDATE onetouch.PS_Students_Debts_Task SET
			note = #{note}
        	, update_id = #{updateId}
        	, update_date = SYSDATE()
        WHERE del_yn = 'N'

            AND id = #{id}
