mapper.update:
UPDATE PS_Students SET

NAME = #{name},


grade = #{grade},


class = #{sClass},


del_yn = #{delYn},

update_id = #{updateId}, update_date = #{updateDate} WHERE ID = #{id};

mapper.select:
SELECT S.*
FROM PS_Students S
where  S.del_yn='N'

AND  S.school_id = #{schoolId}


 AND   S.name = #{name}


 AND   S.grade = #{grade}


 AND   S.class = #{sClass}
