mapper.update:
UPDATE PS_Students SET

NAME = #{name},


grade = #{grade},


class = #{sClass},

update_id = #{updateId}
, update_date = SYSDATE()

WHERE ID = #{id}

    AND  school_id = #{schoolId}

mapper.delete:
UPDATE PS_Students SET
    del_yn = 'Y'
    , update_id = #{updateId}
    , update_date = SYSDATE()
WHERE ID = #{id}

AND  school_id = #{schoolId}

AND del_yn = 'N'

mapper.select:
SELECT S.*,(Select name from PS_School SL where S.school_id=SL.id AND del_yn='N') AS schoolName,LPAD(S.id, 4, '0') as student_id
FROM PS_Students S
where  S.del_yn='N'

AND  S.school_id = #{schoolId}



 AND   UPPER(S.name) like UPPER (#{nameLike})


 AND   S.grade = #{grade}


 AND   S.class = #{sClass}
