mapper.update:
UPDATE PS_Students SET

NAME = #{name},


grade = #{grade},


class = #{sClass},

update_id = #{updateId}
, update_date = #{updateDate}

WHERE ID = #{id}

mapper.delete:
UPDATE PS_Students SET
    del_yn = 'Y'
    , update_id = #{updateId}
    , update_date = #{updateDate}
WHERE ID = #{id}

mapper.select:
SELECT S.*,(Select name from PS_School SL where S.school_id=SL.id AND del_yn='N') AS schoolName
FROM PS_Students S
where  S.del_yn='N'

AND  S.school_id = #{schoolId}



 AND   S.name like #{nameLike}


 AND   S.grade = #{grade}


 AND   S.class = #{sClass}
